print("\nOperacoes basicas: ")
print(2 + 3) #5
print(5 -3) #2
print(10 - 4 + 2) # 8
print(2 * 10) # 20, multiplicação (*)
print(20 / 4) # 5.0, divisão (/)
print(10 / 4) # 2.5, divisão decimal
print(10 // 4) # 2, divisão inteira

print("\nExponenciacao: ")
# Exponenciação
print(2 ** 3) # 2 elevado a 3
print(2 ** 1 / 2) # Exponenciação com expoente fracionário (raiz cúbica de 2)

print("\nDivisao modular: ")
# Resto da divisão
print(10 % 3) #1
print(16 % 7) #2
print(63 % 8) #7
